(507, 1, '2019-07-18 08:55:52', '2019-07-18 05:55:52', '<!-- wp:paragraph {\"align\":\"center\",\"textColor\":\"vivid-cyan-blue\",\"backgroundColor\":\"very-light-gray\"} -->\n<p style=\"text-align:center\" class=\"has-text-color has-background has-vivid-cyan-blue-color has-very-light-gray-background-color\"><strong>Программа\nпрофессиональной переподготовки </strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"textColor\":\"vivid-cyan-blue\",\"backgroundColor\":\"very-light-gray\"} -->\n<p style=\"text-align:center\" class=\"has-text-color has-background has-vivid-cyan-blue-color has-very-light-gray-background-color\"><strong>«</strong><strong>Музыкальный руководитель ДОУ условиях реализации ФГОС</strong><strong>» </strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Актуальность:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"backgroundColor\":\"very-light-gray\"} -->\n<p class=\"has-background has-very-light-gray-background-color\">В соответствии\nс новым законом об образовании первой ступенью образования является дошкольное\nзвено.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"backgroundColor\":\"very-light-gray\"} -->\n<p class=\"has-background has-very-light-gray-background-color\">&nbsp;Осуществление педагогического процесса в условиях введения в\nдействие Федерального государственного образовательного стандарта дошкольного\nобразования (ФГОС ДО), обеспечивающего качество дошкольного образования,\nнапрямую связано с повышением профессиональной компетенции педагогов детского\nсада, необходимой для успешной реализации стандарта. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"backgroundColor\":\"very-light-gray\"} -->\n<p class=\"has-background has-very-light-gray-background-color\">Основной\nцелью является подготовка квалифицированных специалистов, способных осваивать и\nприменять для работы современные технологии.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Содержание:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table {\"backgroundColor\":\"subtle-light-gray\"} -->\n<table class=\"wp-block-table has-subtle-light-gray-background-color has-background\"><tbody><tr><td>\n  <strong>№ п/п</strong>\n  </td><td>\n  <strong>Название модулей</strong>\n  </td><td>\n  <strong>Всего, ауд.часов</strong>\n  </td><td>\n  <strong>Форма контроля</strong>\n  </td></tr><tr><td>\n  Модуль 1\n  </td><td>\n  Нормативные документы &nbsp;\n  </td><td>\n  22\n  </td><td>\n  &nbsp;\n  </td></tr><tr><td>\n  Модуль 2\n  </td><td>\n  Методологические основы моделирования образовательных компонентов\n  музыкального воспитания в соответствии с ФГОС ДО\n  </td><td>\n  22\n  </td><td>\n  &nbsp;\n  </td></tr><tr><td>\n  Модуль 3\n  </td><td>\n  Содержание педагогического процесса музыкального воспитания детей\n  дошкольного возраста\n  </td><td>\n  14\n  </td><td>\n  &nbsp;\n  </td></tr><tr><td>\n  Модуль 4\n  </td><td>\n  Общая и дошкольная педагогика и психология\n  </td><td>\n  22\n  </td><td>\n  &nbsp;\n  </td></tr><tr><td>\n  Модуль 5\n  </td><td>\n  Здоровьесберегающие технологии в ДОУ\n  </td><td>\n  8\n  </td><td>\n  &nbsp;\n  </td></tr><tr><td>\n  Модуль 6\n  </td><td>\n  Инновационные процессы в музыкальном воспитании детей дошкольного\n  возраста\n  </td><td>\n  22\n  </td><td>\n  &nbsp;\n  </td></tr><tr><td>\n  Модуль 7\n  </td><td>\n  Взаимодействие музыкального руководителя с участниками образовательного\n  процесса\n  </td><td>\n  8\n  </td><td>\n  &nbsp;\n  </td></tr><tr><td>\n  <strong>Итоговое тестирование</strong>\n  </td><td>\n  <strong>&nbsp;2</strong>\n  </td><td>\n  <strong>зачет</strong>\n  </td></tr><tr><td>\n  ИТОГО\n  </td><td>\n  &nbsp;\n  </td><td>\n  120\n  </td><td>\n  &nbsp;\n  </td></tr></tbody></table>\n<!-- /wp:table -->', '«Музыкальный руководитель ДОУ условиях реализации ФГОС»', '', 'publish', 'closed', 'closed', '', '%d0%bc%d1%83%d0%b7%d1%8b%d0%ba%d0%b0%d0%bb%d1%8c%d0%bd%d1%8b%d0%b9-%d1%80%d1%83%d0%ba%